import { useDispatch } from "react-redux";
import "./Dialog.css";
import { useSelector } from "react-redux";
import { useState } from "react";
import { openDialog } from "../../redux/dialog/dialogSlice";
import { createProduct } from "../../redux/product/productSlice";
const Dialog = () => {
    const visible = useSelector((state) => state.dialog.dialogVisible);

    useEffect(() => {
        const createProduct = async () => {
            try {
                const res = await axios.post(
                    "https://my-json-server.typicode.com/drakulovski/dbplaceholder/products"
                );

                dispatch(createProduct({ product: product }));
            } catch (err) {
                console.log(err);
            }
        };
        fetchProducts();
    }, []);

    const dispatch = useDispatch();
    const [product, setProduct] = useState({
        title: "",
        price: "",
        image: "",
        description: "",
    });

    const handleSave = async () => {
        try {
            const res = await axios.post(
                "https://my-json-server.typicode.com/drakulovski/dbplaceholder/products"
            );

            dispatch(createProduct({ product: product }));
        } catch (err) {
            console.log(err);
        }
    };

    const handleChange = (e) => {
        setProduct((prev) => ({
            ...prev,
            [e.target.name]: e.target.value,
        }));
    };
    return (
        <div>
            {visible && (
                <div className="dialog-container">
                    <div>
                        <input
                            required
                            type="text"
                            name="title"
                            id="title"
                            onChange={handleChange}
                        />
                    </div>
                    <div>
                        <input
                            required
                            type="number"
                            name="price"
                            id="price"
                            onChange={handleChange}
                        />
                    </div>
                    <div>
                        <input
                            required
                            type="url"
                            name="image"
                            id="image"
                            onChange={handleChange}
                        />
                    </div>
                    <div>
                        <input
                            required
                            type="text"
                            name="description"
                            id="description"
                            onChange={handleChange}
                        />
                    </div>
                    <div>
                        <button onClick={handleSave}>Save</button>
                        <button
                            onClick={() =>
                                dispatch(openDialog({ dialogVisible: false }))
                            }
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};
export default Dialog;
