import { useDispatch } from "react-redux";
import "./Dialog.css";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { openDialog } from "../../redux/dialog/dialogSlice";
import { createProduct } from "../../redux/product/productSlice";
import axios from "axios";

const Dialog = () => {
    const visible = useSelector((state) => state.dialog.dialogVisible);
    const states = useSelector((state) => state.utility.states);
    const categories = useSelector((state) => state.utility.categories);

    const dispatch = useDispatch();
    const [product, setProduct] = useState({
        title: "",
        price: "",
        image: "",
        description: "",
        categoryId: "",
        stateId: "",
    });

    const handleSave = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post(
                "https://my-json-server.typicode.com/drakulovski/dbplaceholder/products"
            );
            console.log(res.data);
            dispatch(createProduct({ product: product }));
            dispatch(openDialog({ dialogVisible: false }));
            setProduct({
                title: "",
                price: "",
                image: "",
                description: "",
                categoryId: "",
                stateId: "",
            });
        } catch (err) {
            console.log(err);
        }
    };

    const handleChange = (e) => {
        setProduct((prev) => ({
            ...prev,
            [e.target.name]: e.target.value,
        }));
    };

    return (
        <div>
            {visible && (
                <div className="dialog-container">
                    <form onSubmit={handleSave}>
                        <select
                            value={product.stateId}
                            name="state"
                            onChange={handleChange}
                        >
                            {states.map((item) => (
                                <option value={item.id} key={item.id}>
                                    {item.name}
                                </option>
                            ))}
                        </select>
                        <select
                            value={product.categoryId}
                            name="category"
                            onChange={handleChange}
                        >
                            {categories.map((item) => (
                                <option value={item.id} key={item.id}>
                                    {item.name}
                                </option>
                            ))}
                        </select>
                        <div>
                            <input
                                required
                                type="text"
                                name="title"
                                id="title"
                                value={product.title}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <input
                                required
                                type="number"
                                name="price"
                                id="price"
                                value={product.price}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <input
                                required
                                type="url"
                                name="image"
                                id="image"
                                value={product.image}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <input
                                required
                                type="text"
                                name="description"
                                id="description"
                                value={product.description}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <button type="submit">Save</button>
                            <button
                                onClick={() =>
                                    dispatch(
                                        openDialog({ dialogVisible: false })
                                    )
                                }
                            >
                                Close
                            </button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};
export default Dialog;
