import { useDispatch } from "react-redux";
import "./Dialog.css";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { openDialog } from "../../redux/dialog/dialogSlice";
import { createProduct } from "../../redux/product/productSlice";
import axios from "axios";

const Dialog = () => {
    const visible = useSelector((state) => state.dialog.dialogVisible);
    const states = useSelector((state) => state.utility.states);
    const categories = useSelector((state) => state.utility.categories);
    const dispatch = useDispatch();

    const [product, setProduct] = useState({
        title: "",
        price: "",
        image: "",
        description: "",
        categoryId: "",
        stateId: "",
    });
    const [validationErrors, setValidationErrors] = useState({
        titleLength: "",
        priceValue: "",
        selectedState: "",
        selectedCategory: "",
        priceWithTax: "",
    });

    const handleSave = async (e) => {
        e.preventDefault();
        console.log("Errors:", validationErrors);
        try {
            const res = await axios.post(
                "https://my-json-server.typicode.com/drakulovski/dbplaceholder/products"
            );
            dispatch(createProduct({ product: product }));
            dispatch(openDialog({ dialogVisible: false }));
            setProduct({
                title: "",
                price: 0,
                image: "",
                description: "",
                categoryId: "Select category",
                stateId: "Select state",
            });
        } catch (err) {
            console.log(err);
        }
    };

    const handleChange = (e) => {
        setProduct((prev) => ({
            ...prev,
            [e.target.name]: e.target.value,
        }));
    };

    useEffect(() => {
        const validateInput = () => {
            if (product.title.length < 4) {
                setValidationErrors((prev) => ({
                    ...prev,
                    titleLength: "Title needs to be 4 characters or longer!",
                }));
            }
            if (product.price <= 4) {
                setValidationErrors((prev) => ({
                    ...prev,
                    priceValue: "Price needs to be higher than 4!",
                }));
            }
            if (!states.find((state) => state.id === product.stateId)) {
                setValidationErrors((prev) => ({
                    ...prev,
                    selectedState: "Please select a state!",
                }));
            }
            if (
                !categories.find(
                    (category) => category.id === product.categoryId
                )
            ) {
                setValidationErrors((prev) => ({
                    ...prev,
                    selectedCategory: "Please select a category!",
                }));
            }
            if (
                states.find((state) => state.id === product.stateId)?.tax >
                    0.25 &&
                product.price <= 7
            ) {
                setValidationErrors((prev) => ({
                    ...prev,
                    priceWithTax:
                        "Price needs to be higher than 7 for states with tax above 0.25!",
                }));
            }
        };
        validateInput();
    }, [product, states, categories]);

    return (
        <div>
            {visible && (
                <div className="dialog-container">
                    <form onSubmit={handleSave}>
                        <select
                            value={product.stateId}
                            name="stateId"
                            onChange={handleChange}
                        >
                            <option value="Select state">Select state</option>
                            {states.map((item) => (
                                <option value={item.id} key={item.id}>
                                    {item.name}
                                </option>
                            ))}
                        </select>
                        <select
                            value={product.categoryId}
                            name="categoryId"
                            onChange={handleChange}
                        >
                            <option value="Select category">
                                Select category
                            </option>
                            {categories.map((item) => (
                                <option value={item.id} key={item.id}>
                                    {item.name}
                                </option>
                            ))}
                        </select>
                        <div>
                            <input
                                required
                                type="text"
                                name="title"
                                id="title"
                                placeholder="Name"
                                value={product.title}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <input
                                required
                                type="number"
                                name="price"
                                id="price"
                                placeholder="Price"
                                value={product.price}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <input
                                required
                                type="url"
                                name="image"
                                id="image"
                                placeholder="Image URL"
                                value={product.image}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <input
                                required
                                type="text"
                                name="description"
                                id="description"
                                placeholder="Description"
                                value={product.description}
                                onChange={handleChange}
                            />
                        </div>
                        <div>
                            <button type="submit">Save</button>
                            <button
                                onClick={() =>
                                    dispatch(
                                        openDialog({ dialogVisible: false })
                                    )
                                }
                            >
                                Close
                            </button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
};
export default Dialog;
